@app.cell
def __():
    def hamming_distance(strand_1: str, strand_2: str):
        """Returns Hamming distance between two DNA strands of equal length."""

        hamming = 0

        for index in range(len(strand_1)):
            if strand_1[index] != strand_2[index]:
                hamming += 1

        return hamming

    return (hamming_distance,)


